void OAIIncomingWebhooksApi::postWebhookAsSlackMessage(const QString &webhook_id, const ::OpenAPI::OptionalParam<QString> &file_url, const ::OpenAPI::OptionalParam<QString> &filename, const ::OpenAPI::OptionalParam<bool> &markdown, const ::OpenAPI::OptionalParam<QString> &subject, const ::OpenAPI::OptionalParam<QString> &text) {
    QString fullPath = QString(_serverConfigs["postWebhookAsSlackMessage"][_serverIndices.value("postWebhookAsSlackMessage")].URL()+"/webhooks/incoming/{webhookId}");
    
    
    {
        QString webhook_idPathParam("{");
        webhook_idPathParam.append("webhookId").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "simple";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "webhookId", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"webhookId"+pathSuffix : pathPrefix;
        fullPath.replace(webhook_idPathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(webhook_id)));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "POST");

    if (file_url.hasValue())
    {
        input.add_var("fileURL", ::OpenAPI::toStringValue(file_url.value()));
    }
    if (filename.hasValue())
    {
        input.add_var("filename", ::OpenAPI::toStringValue(filename.value()));
    }
    if (markdown.hasValue())
    {
        input.add_var("markdown", ::OpenAPI::toStringValue(markdown.value()));
    }
    if (subject.hasValue())
    {
        input.add_var("subject", ::OpenAPI::toStringValue(subject.value()));
    }
    if (text.hasValue())
    {
        input.add_var("text", ::OpenAPI::toStringValue(text.value()));
    }

    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIIncomingWebhooksApi::postWebhookAsSlackMessageCallback);
    connect(this, &OAIIncomingWebhooksApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

void OAIIncomingWebhooksApi::postWebhookAsSlackMessageCallback(OAIHttpRequestWorker *worker) {
    QString error_str = worker->error_str;
    QNetworkReply::NetworkError error_type = worker->error_type;

    if (worker->error_type != QNetworkReply::NoError) {
        error_str = QString("%1, %2").arg(worker->error_str, QString(worker->response));
    }
    worker->deleteLater();

    if (worker->error_type == QNetworkReply::NoError) {
        Q_EMIT postWebhookAsSlackMessageSignal();
        Q_EMIT postWebhookAsSlackMessageSignalFull(worker);
    } else {

#if defined(_MSC_VER)
// For MSVC
#pragma warning(push)
#pragma warning(disable : 4996)
#elif defined(__clang__)
// For Clang
#pragma clang diagnostic push
#pragma clang diagnostic ignored "-Wdeprecated-declarations"
#elif defined(__GNUC__)
// For GCC
#pragma GCC diagnostic push
#pragma GCC diagnostic ignored "-Wdeprecated-declarations"
#endif

        Q_EMIT postWebhookAsSlackMessageSignalE(error_type, error_str);
        Q_EMIT postWebhookAsSlackMessageSignalEFull(worker, error_type, error_str);

#if defined(_MSC_VER)
#pragma warning(pop)
#elif defined(__clang__)
#pragma clang diagnostic pop
#elif defined(__GNUC__)
#pragma GCC diagnostic pop
#endif

        Q_EMIT postWebhookAsSlackMessageSignalError(error_type, error_str);
        Q_EMIT postWebhookAsSlackMessageSignalErrorFull(worker, error_type, error_str);
    }
}

void OAIIncomingWebhooksApi::tokenAvailable(){

    oauthToken token;
    switch (_OauthMethod) {
    case 1: //implicit flow
        token = _implicitFlow.getToken(_latestScope.join(" "));
        if(token.isValid()){
            _latestInput.headers.insert("Authorization", "Bearer " + token.getToken());
            _latestWorker->execute(&_latestInput);
        }else{
            _implicitFlow.removeToken(_latestScope.join(" "));
            qDebug() << "Could not retrieve a valid token";
        }
        break;
    case 2: //authorization flow
        token = _authFlow.getToken(_latestScope.join(" "));
        if(token.isValid()){
            _latestInput.headers.insert("Authorization", "Bearer " + token.getToken());
            _latestWorker->execute(&_latestInput);
        }else{
            _authFlow.removeToken(_latestScope.join(" "));
            qDebug() << "Could not retrieve a valid token";
        }
        break;
    case 3: //client credentials flow
        token = _credentialFlow.getToken(_latestScope.join(" "));
        if(token.isValid()){
            _latestInput.headers.insert("Authorization", "Bearer " + token.getToken());
            _latestWorker->execute(&_latestInput);
        }else{
            _credentialFlow.removeToken(_latestScope.join(" "));
            qDebug() << "Could not retrieve a valid token";
        }
        break;
    case 4: //resource owner password flow
        token = _passwordFlow.getToken(_latestScope.join(" "));
        if(token.isValid()){
            _latestInput.headers.insert("Authorization", "Bearer " + token.getToken());
            _latestWorker->execute(&_latestInput);
        }else{
            _credentialFlow.removeToken(_latestScope.join(" "));
            qDebug() << "Could not retrieve a valid token";
        }
        break;
    default:
        qDebug() << "No Oauth method set!";
        break;
    }
}
