void OAIOpportunitiesApi::getOpportunitiesTasksTaskId(const qint32 &task_id, const ::OpenAPI::OptionalParam<QString> &datasource, const ::OpenAPI::OptionalParam<QString> &if_none_match) {
    QString fullPath = QString(_serverConfigs["getOpportunitiesTasksTaskId"][_serverIndices.value("getOpportunitiesTasksTaskId")].URL()+"/opportunities/tasks/{task_id}/");
    
    
    {
        QString task_idPathParam("{");
        task_idPathParam.append("task_id").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "task_id", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"task_id"+pathSuffix : pathPrefix;
        fullPath.replace(task_idPathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(task_id)));
    }
    QString queryPrefix, querySuffix, queryDelimiter, queryStyle;
    if (datasource.hasValue())
    {
        queryStyle = "";
        if (queryStyle == "")
            queryStyle = "form";
        queryPrefix = getParamStylePrefix(queryStyle);
        querySuffix = getParamStyleSuffix(queryStyle);
        queryDelimiter = getParamStyleDelimiter(queryStyle, "datasource", false);
        if (fullPath.indexOf("?") > 0)
            fullPath.append(queryPrefix);
        else
            fullPath.append("?");

        fullPath.append(QUrl::toPercentEncoding("datasource")).append(querySuffix).append(QUrl::toPercentEncoding(datasource.stringValue()));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "GET");


    if (if_none_match.hasValue())
    {
        if (!::OpenAPI::toStringValue(if_none_match.value()).isEmpty()) {
            input.headers.insert("If-None-Match", ::OpenAPI::toStringValue(if_none_match.value()));
        }
        }
    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIOpportunitiesApi::getOpportunitiesTasksTaskIdCallback);
    connect(this, &OAIOpportunitiesApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

