void OAIMiscellaneousApi::renderMarkup(const ::OpenAPI::OptionalParam<OAIMarkupOption> &oai_markup_option) {
    QString fullPath = QString(_serverConfigs["renderMarkup"][_serverIndices.value("renderMarkup")].URL()+"/markup");
    
    if (_apiKeys.contains("TOTPHeader")) {
        addHeaders("TOTPHeader",_apiKeys.find("TOTPHeader").value());
    }
    
    if (_apiKeys.contains("AuthorizationHeaderToken")) {
        addHeaders("AuthorizationHeaderToken",_apiKeys.find("AuthorizationHeaderToken").value());
    }
    
    if (_apiKeys.contains("SudoHeader")) {
        addHeaders("SudoHeader",_apiKeys.find("SudoHeader").value());
    }
    
    if (!_username.isEmpty() && !_password.isEmpty()) {
        QByteArray b64;
        b64.append(_username.toUtf8() + ":" + _password.toUtf8());
        addHeaders("Authorization","Basic " + b64.toBase64());
    }
    if (_apiKeys.contains("AccessToken")) {
        if (fullPath.indexOf("?") > 0)
            fullPath.append("&");
        else
            fullPath.append("?");
        fullPath.append("AccessToken=").append(_apiKeys.find("AccessToken").value());
    }
    
    if (_apiKeys.contains("SudoParam")) {
        if (fullPath.indexOf("?") > 0)
            fullPath.append("&");
        else
            fullPath.append("?");
        fullPath.append("SudoParam=").append(_apiKeys.find("SudoParam").value());
    }
    
    if (_apiKeys.contains("Token")) {
        if (fullPath.indexOf("?") > 0)
            fullPath.append("&");
        else
            fullPath.append("?");
        fullPath.append("Token=").append(_apiKeys.find("Token").value());
    }
    
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "POST");

    if (oai_markup_option.hasValue()){

        
        QByteArray output = oai_markup_option.value().asJson().toUtf8();
        input.request_body.append(output);
    }
    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIMiscellaneousApi::renderMarkupCallback);
    connect(this, &OAIMiscellaneousApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

