async def test_mark_played_item(client):
    """Test case for mark_played_item

    Marks an item as played for user.
    """
    params = [('datePlayed', '2013-10-20T19:20:30+01:00')]
    headers = { 
        'Accept': 'application/json',
        'CustomAuthentication': 'special-key',
    }
    response = await client.request(
        method='POST',
        path='/Users/{user_id}/PlayedItems/{item_id}'.format(user_id='user_id_example', item_id='item_id_example'),
        headers=headers,
        params=params,
        )
    assert response.status == 200, 'Response body is : ' + (await response.read()).decode('utf-8')


