void OAIOSDBApi::unloadService(const QString &service_id) {
    QString fullPath = QString(_serverConfigs["unloadService"][_serverIndices.value("unloadService")].URL()+"/api/v1/services/{serviceId}");
    
    
    {
        QString service_idPathParam("{");
        service_idPathParam.append("serviceId").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "simple";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "serviceId", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"serviceId"+pathSuffix : pathPrefix;
        fullPath.replace(service_idPathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(service_id)));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "DELETE");


    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIOSDBApi::unloadServiceCallback);
    connect(this, &OAIOSDBApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

