void OAIIndexOperationsApi::describeIndex(const QString &index_name) {
    QString fullPath = QString(_serverConfigs["describeIndex"][_serverIndices.value("describeIndex")].URL()+"/databases/{indexName}");
    
    if (_apiKeys.contains("ApiKey")) {
        addHeaders("ApiKey",_apiKeys.find("ApiKey").value());
    }
    
    
    {
        QString index_namePathParam("{");
        index_namePathParam.append("indexName").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "simple";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "indexName", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"indexName"+pathSuffix : pathPrefix;
        fullPath.replace(index_namePathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(index_name)));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "GET");


    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIIndexOperationsApi::describeIndexCallback);
    connect(this, &OAIIndexOperationsApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

