void OAITemplatesAPIApi::updateTemplate(const QString &x_postmark_server_token, const QString &template_id_or_alias, const OAIEditTemplateRequest &body) {
    QString fullPath = QString(_serverConfigs["updateTemplate"][_serverIndices.value("updateTemplate")].URL()+"/templates/{templateIdOrAlias}");
    
    
    {
        QString template_id_or_aliasPathParam("{");
        template_id_or_aliasPathParam.append("templateIdOrAlias").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "templateIdOrAlias", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"templateIdOrAlias"+pathSuffix : pathPrefix;
        fullPath.replace(template_id_or_aliasPathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(template_id_or_alias)));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "PUT");

    {

        
        QByteArray output = body.asJson().toUtf8();
        input.request_body.append(output);
    }
    
    {
        if (!::OpenAPI::toStringValue(x_postmark_server_token).isEmpty()) {
            input.headers.insert("X-Postmark-Server-Token", ::OpenAPI::toStringValue(x_postmark_server_token));
        }
        }
    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAITemplatesAPIApi::updateTemplateCallback);
    connect(this, &OAITemplatesAPIApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

