void OAISettlementApi::getSettlementSummary(const QString &quarter, const ::OpenAPI::OptionalParam<QString> &moss_country_code, const ::OpenAPI::OptionalParam<QString> &tax_region, const ::OpenAPI::OptionalParam<QString> &start_month, const ::OpenAPI::OptionalParam<QString> &end_month) {
    QString fullPath = QString(_serverConfigs["getSettlementSummary"][_serverIndices.value("getSettlementSummary")].URL()+"/api/v1/settlement/summary/{quarter}");
    
    
    {
        QString quarterPathParam("{");
        quarterPathParam.append("quarter").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "quarter", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"quarter"+pathSuffix : pathPrefix;
        fullPath.replace(quarterPathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(quarter)));
    }
    QString queryPrefix, querySuffix, queryDelimiter, queryStyle;
    if (moss_country_code.hasValue())
    {
        queryStyle = "";
        if (queryStyle == "")
            queryStyle = "form";
        queryPrefix = getParamStylePrefix(queryStyle);
        querySuffix = getParamStyleSuffix(queryStyle);
        queryDelimiter = getParamStyleDelimiter(queryStyle, "moss_country_code", false);
        if (fullPath.indexOf("?") > 0)
            fullPath.append(queryPrefix);
        else
            fullPath.append("?");

        fullPath.append(QUrl::toPercentEncoding("moss_country_code")).append(querySuffix).append(QUrl::toPercentEncoding(moss_country_code.stringValue()));
    }
    if (tax_region.hasValue())
    {
        queryStyle = "";
        if (queryStyle == "")
            queryStyle = "form";
        queryPrefix = getParamStylePrefix(queryStyle);
        querySuffix = getParamStyleSuffix(queryStyle);
        queryDelimiter = getParamStyleDelimiter(queryStyle, "tax_region", false);
        if (fullPath.indexOf("?") > 0)
            fullPath.append(queryPrefix);
        else
            fullPath.append("?");

        fullPath.append(QUrl::toPercentEncoding("tax_region")).append(querySuffix).append(QUrl::toPercentEncoding(tax_region.stringValue()));
    }
    if (start_month.hasValue())
    {
        queryStyle = "";
        if (queryStyle == "")
            queryStyle = "form";
        queryPrefix = getParamStylePrefix(queryStyle);
        querySuffix = getParamStyleSuffix(queryStyle);
        queryDelimiter = getParamStyleDelimiter(queryStyle, "start_month", false);
        if (fullPath.indexOf("?") > 0)
            fullPath.append(queryPrefix);
        else
            fullPath.append("?");

        fullPath.append(QUrl::toPercentEncoding("start_month")).append(querySuffix).append(QUrl::toPercentEncoding(start_month.stringValue()));
    }
    if (end_month.hasValue())
    {
        queryStyle = "";
        if (queryStyle == "")
            queryStyle = "form";
        queryPrefix = getParamStylePrefix(queryStyle);
        querySuffix = getParamStyleSuffix(queryStyle);
        queryDelimiter = getParamStyleDelimiter(queryStyle, "end_month", false);
        if (fullPath.indexOf("?") > 0)
            fullPath.append(queryPrefix);
        else
            fullPath.append("?");

        fullPath.append(QUrl::toPercentEncoding("end_month")).append(querySuffix).append(QUrl::toPercentEncoding(end_month.stringValue()));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "GET");


    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAISettlementApi::getSettlementSummaryCallback);
    connect(this, &OAISettlementApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

