void OAIRoadApi::roadStatus(const QList<QString> &ids, const ::OpenAPI::OptionalParam<QDateTime> &date_range_nullable_start_date, const ::OpenAPI::OptionalParam<QDateTime> &date_range_nullable_end_date) {
    QString fullPath = QString(_serverConfigs["roadStatus"][_serverIndices.value("roadStatus")].URL()+"/Road/{ids}/Status");
    
    
    {
        QString idsPathParam("{");
        idsPathParam.append("ids").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "simple";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "ids", false);
        if (ids.size() > 0) {
            QString paramString = (pathStyle == "matrix") ? pathPrefix+"ids"+pathSuffix : pathPrefix;
            qint32 count = 0;
            for (QString t : ids) {
                if (count > 0) {
                    fullPath.append(pathDelimiter);
                }
                fullPath.append(QUrl::toPercentEncoding(::OpenAPI::toStringValue(t)));
                count++;
            }
            fullPath.replace(idsPathParam, paramString);
        }
    }
    QString queryPrefix, querySuffix, queryDelimiter, queryStyle;
    if (date_range_nullable_start_date.hasValue())
    {
        queryStyle = "form";
        if (queryStyle == "")
            queryStyle = "form";
        queryPrefix = getParamStylePrefix(queryStyle);
        querySuffix = getParamStyleSuffix(queryStyle);
        queryDelimiter = getParamStyleDelimiter(queryStyle, "dateRangeNullable.startDate", true);
        if (fullPath.indexOf("?") > 0)
            fullPath.append(queryPrefix);
        else
            fullPath.append("?");

        fullPath.append(QUrl::toPercentEncoding("dateRangeNullable.startDate")).append(querySuffix).append(QUrl::toPercentEncoding(date_range_nullable_start_date.stringValue()));
    }
    if (date_range_nullable_end_date.hasValue())
    {
        queryStyle = "form";
        if (queryStyle == "")
            queryStyle = "form";
        queryPrefix = getParamStylePrefix(queryStyle);
        querySuffix = getParamStyleSuffix(queryStyle);
        queryDelimiter = getParamStyleDelimiter(queryStyle, "dateRangeNullable.endDate", true);
        if (fullPath.indexOf("?") > 0)
            fullPath.append(queryPrefix);
        else
            fullPath.append("?");

        fullPath.append(QUrl::toPercentEncoding("dateRangeNullable.endDate")).append(querySuffix).append(QUrl::toPercentEncoding(date_range_nullable_end_date.stringValue()));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "GET");


    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIRoadApi::roadStatusCallback);
    connect(this, &OAIRoadApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

