void OAIVideoV1CompositionHookApi::updateCompositionHook(const QString &sid, const QString &friendly_name, const ::OpenAPI::OptionalParam<QList<QString>> &audio_sources, const ::OpenAPI::OptionalParam<QList<QString>> &audio_sources_excluded, const ::OpenAPI::OptionalParam<bool> &enabled, const ::OpenAPI::OptionalParam<OAIComposition_hook_enum_format> &format, const ::OpenAPI::OptionalParam<QString> &resolution, const ::OpenAPI::OptionalParam<QString> &status_callback, const ::OpenAPI::OptionalParam<QString> &status_callback_method, const ::OpenAPI::OptionalParam<bool> &trim, const ::OpenAPI::OptionalParam<QJsonValue> &video_layout) {
    QString fullPath = QString(_serverConfigs["updateCompositionHook"][_serverIndices.value("updateCompositionHook")].URL()+"/v1/CompositionHooks/{Sid}");
    
    if (!_username.isEmpty() && !_password.isEmpty()) {
        QByteArray b64;
        b64.append(_username.toUtf8() + ":" + _password.toUtf8());
        addHeaders("Authorization","Basic " + b64.toBase64());
    }
    
    {
        QString sidPathParam("{");
        sidPathParam.append("Sid").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "simple";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "Sid", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"Sid"+pathSuffix : pathPrefix;
        fullPath.replace(sidPathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(sid)));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "POST");

    if (audio_sources.hasValue())
    {
        input.add_var("AudioSources", ::OpenAPI::toStringValue(audio_sources.value()));
    }
    if (audio_sources_excluded.hasValue())
    {
        input.add_var("AudioSourcesExcluded", ::OpenAPI::toStringValue(audio_sources_excluded.value()));
    }
    if (enabled.hasValue())
    {
        input.add_var("Enabled", ::OpenAPI::toStringValue(enabled.value()));
    }
    if (format.hasValue())
    {
        input.add_var("Format", ::OpenAPI::toStringValue(format.value()));
    }
    
    {
        input.add_var("FriendlyName", ::OpenAPI::toStringValue(friendly_name));
    }
    if (resolution.hasValue())
    {
        input.add_var("Resolution", ::OpenAPI::toStringValue(resolution.value()));
    }
    if (status_callback.hasValue())
    {
        input.add_var("StatusCallback", ::OpenAPI::toStringValue(status_callback.value()));
    }
    if (status_callback_method.hasValue())
    {
        input.add_var("StatusCallbackMethod", ::OpenAPI::toStringValue(status_callback_method.value()));
    }
    if (trim.hasValue())
    {
        input.add_var("Trim", ::OpenAPI::toStringValue(trim.value()));
    }
    if (video_layout.hasValue())
    {
        input.add_var("VideoLayout", ::OpenAPI::toStringValue(video_layout.value()));
    }

    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIVideoV1CompositionHookApi::updateCompositionHookCallback);
    connect(this, &OAIVideoV1CompositionHookApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

